import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Home() {
  const [portfolioData, setPortfolioData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get('http://localhost:5000/api/portfolio');
        setPortfolioData(result.data);
      } catch (err) {
        console.error("Error fetching portfolio data:", err);
        setError("Failed to load portfolio data. Please try again later.");
      }
    };
    fetchData();
  }, []);

  if (error) return <div>Error: {error}</div>;
  if (!portfolioData) return <div>Loading...</div>;

  return (
    <div>
      <h1>My Portfolio</h1>
      <section>
        <h2>About Me</h2>
        <p>{portfolioData.about}</p>
      </section>
      <section>
        <h2>Projects</h2>
        {portfolioData.projects.map((project, index) => (
          <div key={index}>
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <a href={project.projectUrl} target="_blank" rel="noopener noreferrer">View Project</a>
          </div>
        ))}
      </section>
      <section>
        <h2>Skills</h2>
        <ul>
          {portfolioData.skills.map((skill, index) => (
            <li key={index}>{skill}</li>
          ))}
        </ul>
      </section>
      <section>
        <h2>Contact</h2>
        <p>Email: {portfolioData.contact.email}</p>
        <p>Phone: {portfolioData.contact.phone}</p>
        <p>LinkedIn: <a href={portfolioData.contact.linkedin} target="_blank" rel="noopener noreferrer">LinkedIn Profile</a></p>
        <p>GitHub: <a href={portfolioData.contact.github} target="_blank" rel="noopener noreferrer">GitHub Profile</a></p>
      </section>
    </div>
  );
}

export default Home;
