import React from 'react';
import '../styles/Service.css';

function Projects() {
  const projects = [
    {
      title: "E-commerce Website",
      description: "A full-stack e-commerce platform built with React and Node.js.",
      icon: "üõí",
      color: "#4CAF50",
      link: "https://github.com/yourusername/ecommerce-project",
      image: "/images/ecommerce-project.jpg" // Add the path to your project image
    },
    {
      title: "Weather App",
      description: "A responsive weather application using OpenWeatherMap API.",
      icon: "üå§Ô∏è",
      color: "#2196F3",
      link: "https://github.com/yourusername/weather-app"
    },
    {
      title: "Task Manager",
      description: "A MERN stack task management application with user authentication.",
      icon: "üìù",
      color: "#FF9800",
      link: "https://github.com/yourusername/task-manager"
    },
    {
      title: "Portfolio Website",
      description: "A personal portfolio website showcasing my projects and skills.",
      icon: "üíº",
      color: "#E91E63",
      link: "https://github.com/yourusername/portfolio-website"
    }
  ];

  return (
    <div className="project-container">
      <h1>My Projects</h1>
      <div className="projects-grid">
        {projects.map((project, index) => (
          <a href={project.link} target="_blank" rel="noopener noreferrer" key={index} className="project-card" style={{backgroundColor: project.color}}>
            <div className="project-content">
              <div className="project-icon">{project.icon}</div>
              <h2>{project.title}</h2>
              <p>{project.description}</p>
            </div>
            <div className="project-overlay">
              <span>View Project</span>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default Projects;
