import React from 'react';
import '../styles/Service.css';

function Service() {
  const projects = [
    {
      title: "Web App",
      description: "A responsive web application built with React and Node.js.",
      icon: "ðŸ’»",
      color: "#4CAF50",
      link: "https://project1.com"
    },
    {
      title: "Mobile App",
      description: "Cross-platform mobile app developed using React Native.",
      icon: "ðŸ“±",
      color: "#2196F3",
      link: "https://project2.com"
    },
    {
      title: "E-commerce Site",
      description: "Full-stack e-commerce solution with secure payment integration.",
      icon: "ðŸ›’",
      color: "#FF9800",
      link: "https://project3.com"
    },
    {
      title: "Portfolio Website",
      description: "Sleek and modern portfolio website showcasing my work.",
      icon: "ðŸŽ¨",
      color: "#E91E63",
      link: "https://project4.com"
    }
  ];

  return (
    <div className="project-container">
      <h1>My Projects</h1>
      <div className="projects-grid">
        {projects.map((project, index) => (
          <a href={project.link} target="_blank" rel="noopener noreferrer" key={index} className="project-card" style={{backgroundColor: project.color}}>
            <div className="project-content">
              <div className="project-icon">{project.icon}</div>
              <h2>{project.title}</h2>
              <p>{project.description}</p>
            </div>
            <div className="project-overlay">
              <span>View Project</span>
            </div>
          </a>
        ))}
      </div>
    </div>
  );
}

export default Service;
