import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCode, faPaintBrush, faCamera, faTasks } from '@fortawesome/free-solid-svg-icons';
import '../styles/About.css';
import Pavan from '../images/Pavan.jpg';

function About() {
  const skills = [
    { name: 'Web Development', icon: faCode },
    { name: 'Design', icon: faPaintBrush },
    { name: 'Photography', icon: faCamera },
    { name: 'Project Management', icon: faTasks },
  ];

  return (
    <div className="about-container">
      <div className="about-content">
        <div className="profile-image">
          <img src={Pavan} alt="Your Name" />
        </div>
        <div className="about-description">
          <h2>About Me</h2>
          <p>
          Hello! I'm Pavan Kumar Maruboina, a passionate BTech student specializing in AI/ML 
          within the Computer Science and Engineering stream. 
          I am actively honing my skills in WE, exploring innovative solutions, and continuously learning about the latest advancements in the field.
          </p>
          <p>
            My approach combines creativity with technical expertise to deliver 
            innovative solutions that meet and exceed client expectations. I'm constantly 
            learning and adapting to new technologies to stay at the forefront of the industry.
          </p>
        </div>
      </div>
      <div className="skills-section">
        <h3>My Skills</h3>
        <div className="skills-icons">
          {skills.map((skill, index) => (
            <div key={index} className="skill-item">
              <FontAwesomeIcon icon={skill.icon} />
              <span>{skill.name}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default About;
