import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCode, faMobileAlt, faServer, faDatabase } from '@fortawesome/free-solid-svg-icons';
import '../styles/Services.css';

const services = [
  {
    icon: faCode,
    title: "Web Development",
    description: "Creating responsive and dynamic websites using modern frameworks and technologies."
  },
  {
    icon: faMobileAlt,
    title: "Mobile App Development",
    description: "Developing cross-platform mobile applications for iOS and Android devices."
  },
  {
    icon: faServer,
    title: "Backend Development",
    description: "Building robust server-side applications and APIs to power your web and mobile apps."
  },
  {
    icon: faDatabase,
    title: "Database Management",
    description: "Designing and optimizing databases for efficient data storage and retrieval."
  }
];

function Services() {
  return (
    <div className="services-container">
      <h1>My Services</h1>
      <div className="services-grid">
        {services.map((service, index) => (
          <div key={index} className="service-card">
            <FontAwesomeIcon icon={service.icon} className="service-icon" />
            <h3>{service.title}</h3>
            <p>{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Services;
