import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLinkedin, faFacebookSquare, faTwitter, faInstagram } from '@fortawesome/free-brands-svg-icons';
import { faDownload } from '@fortawesome/free-solid-svg-icons';
import '../styles/Home.css';

function Home() {
  const handleDownloadCV = () => {
    const link = document.createElement('a');
    link.href = '/MPavanKumarResume.pdf'; // Update this path
    link.download = 'Pavan_kumar_Maruboina.pdf';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const socialLinks = [
    { icon: faLinkedin, url: 'https://www.linkedin.com/in/pavan-kumar-maruboina-685765271/' },
    { icon: faFacebookSquare, url: 'https://www.facebook.com/yourusername' },
    { icon: faTwitter, url: 'https://twitter.com/yourusername' },
    { icon: faInstagram, url: 'https://www.instagram.com/yourusername' },
  ];

  return (
    <div className="home-container">
      <div className="content">
        <h2>HELLO</h2>
        <h1>I'm <span className="highlight">Pavan </span></h1>
        <p>This is Your Name, visual designer, Photographer, Web Developer and Creative Director located in Your Location, looking for working around the globe.</p>
        <button className="download-cv" onClick={handleDownloadCV}>
          <FontAwesomeIcon icon={faDownload} /> DOWNLOAD CV
        </button>
        <div className="social-icons">
          {socialLinks.map((link, index) => (
            <a key={index} href={link.url} target="_blank" rel="noopener noreferrer" className="social-icon">
              <FontAwesomeIcon icon={link.icon} />
            </a>
          ))}
        </div>
      </div>
      <div className="image-container">
        {/* Add your profile image here */}
      </div>
    </div>
  );
}

export default Home;
