import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Carousel } from 'react-bootstrap';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../styles/global.css';
import '../styles/Home.css';

function Home() {
  const [portfolioData, setPortfolioData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios.get('http://localhost:5000/api/portfolio');
        setPortfolioData(result.data);
      } catch (err) {
        console.error("Error fetching portfolio data:", err);
        setError("Failed to load portfolio data. Please try again later.");
      }
    };
    fetchData();
  }, []);

  if (error) return <div className="container mt-5 text-center text-danger">{error}</div>;
  if (!portfolioData) return <div className="container mt-5 text-center">Loading...</div>;

  return (
    <div className="home-container">
      <Carousel className="mb-5">
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400?text=Welcome"
            alt="Welcome"
          />
          <Carousel.Caption>
            <h3>Welcome to My Portfolio</h3>
            <p>Discover my projects and skills</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400?text=Projects"
            alt="Projects"
          />
          <Carousel.Caption>
            <h3>My Projects</h3>
            <p>Check out my latest work</p>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="https://via.placeholder.com/800x400?text=Skills"
            alt="Skills"
          />
          <Carousel.Caption>
            <h3>My Skills</h3>
            <p>Explore my technical expertise</p>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>

      <div className="container mt-5">
        <section id="about" className="mb-5 animated-section">
          <h2 className="section-title">About Me</h2>
          <p className="lead">{portfolioData.about}</p>
          <a href="/path-to-your-resume.pdf" download className="btn btn-primary mt-3 animated-button">Download Resume</a>
        </section>
        
        <section id="projects" className="mb-5 animated-section">
          <h2 className="section-title">Projects</h2>
          <div className="row">
            {portfolioData.projects.map((project, index) => (
              <div key={index} className="col-md-6 col-lg-4 mb-4">
                <div className="card h-100 project-card">
                  <div className="card-body">
                    <h3 className="card-title">{project.title}</h3>
                    <p className="card-text">{project.description}</p>
                  </div>
                  <div className="card-footer bg-transparent border-top-0">
                    <a href={project.projectUrl} className="btn btn-primary animated-button" target="_blank" rel="noopener noreferrer">View Project</a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
        
        <section id="skills" className="mb-5 animated-section">
          <h2 className="section-title">Skills</h2>
          <div>
            {portfolioData.skills.map((skill, index) => (
              <span key={index} className="badge bg-secondary skill-badge">{skill}</span>
            ))}
          </div>
        </section>
        
        <section id="contact" className="mb-5 animated-section">
          <h2 className="section-title">Contact</h2>
          <ul className="list-unstyled">
            <li><i className="bi bi-envelope contact-icon"></i> {portfolioData.contact.email}</li>
            <li><i className="bi bi-phone contact-icon"></i> {portfolioData.contact.phone}</li>
            <li><i className="bi bi-linkedin contact-icon"></i> <a href={portfolioData.contact.linkedin} target="_blank" rel="noopener noreferrer">LinkedIn Profile</a></li>
            <li><i className="bi bi-github contact-icon"></i> <a href={portfolioData.contact.github} target="_blank" rel="noopener noreferrer">GitHub Profile</a></li>
          </ul>
        </section>
      </div>
      <Footer />
    </div>
  );
}

export default Home;
