import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [portfolioData, setPortfolioData] = useState(null);

  useEffect(() => {
    fetchPortfolioData();
  }, []);

  const fetchPortfolioData = async () => {
    try {
      const response = await axios.get('/api/portfolio');
      setPortfolioData(response.data);
    } catch (error) {
      console.error('Error fetching portfolio data:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put('/api/admin/portfolio', portfolioData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      alert('Portfolio updated successfully');
    } catch (error) {
      alert('Failed to update portfolio');
    }
  };

  if (!portfolioData) return <div>Loading...</div>;

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit Portfolio</h2>
      <textarea
        value={portfolioData.about}
        onChange={(e) => setPortfolioData({ ...portfolioData, about: e.target.value })}
        placeholder="About"
      />
      {/* Add more fields for projects, skills, and contact information */}
      <button type="submit">Update Portfolio</button>
    </form>
  );
}

export default AdminDashboard;
