import React from 'react';
import '../styles/Home.css';

function Home() {
  const handleDownloadCV = () => {
    // Create a link element
    const link = document.createElement('a');
    // Set the href to the path of your CV file
    link.href = '/client\public\MPavanKumarResume.pdf';
    // Set the download attribute with the desired file name
    link.download = 'Pavan_kumar_Maruboina.pdf';
    // Append to body, click, and remove
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="home-container">
      <div className="content">
        <h2>HELLO</h2>
        <h1>I'm <span className="highlight">Your Name</span></h1>
        <p>This is Your Name, visual designer, Photographer, Web Developer and Creative Director located in Your Location, looking for working around the globe.</p>
        <button className="download-cv" onClick={handleDownloadCV}>DOWNLOAD CV</button>
        <div className="social-icons">
          {/* Add your social media icons here */}
        </div>
      </div>
      <div className="image-container">
        {/* Add your profile image here */}
      </div>
    </div>
  );
}

export default Home;
